mapper.select:
SELECT board_no boardNo , board_title boardTitle FROM board
board_title like #{searchWord}
ORDER BY board_no LIMIT #{beginRow}, #{rowPerPage}

mapper.insert:
INSERT INTO board (
	board_title, board_content, board_user
) VALUES (
 #{boardTitle}, #{boardContent}, #{boardUser}
)

mapper.update:
UPDATE board
board_title = #{boardTitle}, board_Content = #{boardContent}, board_User = #{boardUser},
WHERE board_no = #{boardNo}
